src/api/getWorkOrder.ts.content:
import { z } from 'zod';
import { createEndpoint, WorkOrders, FormSubmissions, BatterySwaps, PartsUsed } from 'zite-integrations-backend-sdk';

export default createEndpoint({
  authenticated: true,
  description: 'Fetch work order with all related submissions',
  inputSchema: z.object({
    workOrderId: z.string(),
  }),
  outputSchema: z.object({
    workOrder: z.object({
      id: z.string(),
      workOrderId: z.string(),
      status: z.string(),
      workType: z.string(),
      initialIssue: z.string().optional(),
      pointCode: z.string().optional(),
      lockerVersion: z.string().optional(),
      client: z.string().optional(),
      createdAt: z.string().optional(),
      completedAt: z.string().optional(),
    }),
    formSubmissions: z.array(z.object({
      id: z.string(),
      formType: z.string().optional(),
      submittedAt: z.string().optional(),
    })),
    batterySwaps: z.array(z.object({
      id: z.string(),
      oldBatterySn: z.string().optional(),
      newBatterySn: z.string().optional(),
      recordedAt: z.string().optional(),
    })),
    partsUsed: z.array(z.object({
      id: z.string(),
      partName: z.string().optional(),
      quantity: z.number().optional(),
      recordedAt: z.string().optional(),
    })),
  }),
  execute: async ({ input }) => {
    const workOrder = await WorkOrders.findOne({
      filters: { workOrderId: input.workOrderId }
    });

    if (!workOrder) {
      throw new Error('Work order not found');
    }

    // Fetch related records
    const formSubmissions = await FormSubmissions.findAll({
      filters: { workOrder: workOrder.id }
    });

    const batterySwaps = await BatterySwaps.findAll({
      filters: { workOrder: workOrder.id }
    });

    const partsUsed = await PartsUsed.findAll({
      filters: { workOrder: workOrder.id }
    });

    return {
      workOrder: {
        id: workOrder.id,
        workOrderId: workOrder.workOrderId || '',
        status: workOrder.status || '',
        workType: workOrder.workType || '',
        initialIssue: workOrder.initialIssue,
        pointCode: workOrder.pointCode,
        lockerVersion: workOrder.lockerVersion,
        client: workOrder.client,
        createdAt: workOrder.createdAt,
        completedAt: workOrder.completedAt,
      },
      formSubmissions: formSubmissions.records.map(fs => ({
        id: fs.id,
        formType: fs.formType,
        submittedAt: fs.submittedAt,
      })),
      batterySwaps: batterySwaps.records.map(bs => ({
        id: bs.id,
        oldBatterySn: bs.oldBatterySn,
        newBatterySn: bs.newBatterySn,
        recordedAt: bs.recordedAt,
      })),
      partsUsed: partsUsed.records.map(pu => ({
        id: pu.id,
        partName: pu.partName,
        quantity: pu.quantity,
        recordedAt: pu.recordedAt,
      })),
    };
  },
});

src/api/initWorkOrder.ts.content:
import { z } from 'zod';
import { createEndpoint, WorkOrders } from 'zite-integrations-backend-sdk';

export default createEndpoint({
  authenticated: true,
  description: 'Initialize or retrieve a work order from URL parameters',
  inputSchema: z.object({
    woId: z.string(),
    woType: z.enum(['battery_swap', 'wind_audit', 'work_order']).default('work_order'),
    issue: z.string().optional(),
    pointCode: z.string().optional(),
    lockerVersion: z.string().optional(),
    client: z.string().optional(),
  }),
  outputSchema: z.object({
    workOrder: z.object({
      id: z.string(),
      workOrderId: z.string(),
      status: z.string(),
      workType: z.string(),
      initialIssue: z.string().optional(),
      pointCode: z.string().optional(),
      lockerVersion: z.string().optional(),
      client: z.string().optional(),
    }),
    initialFormType: z.string(),
  }),
  execute: async ({ input, context }) => {
    // Check if work order already exists
    const existing = await WorkOrders.findOne({
      filters: { workOrderId: input.woId }
    });

    if (existing) {
      return {
        workOrder: {
          id: existing.id,
          workOrderId: existing.workOrderId || input.woId,
          status: existing.status || 'In Progress',
          workType: existing.workType || input.woType,
          initialIssue: existing.initialIssue,
          pointCode: existing.pointCode,
          lockerVersion: existing.lockerVersion,
          client: existing.client,
        },
        initialFormType: input.woType,
      };
    }

    // Create new work order
    const workOrder = await WorkOrders.create({
      record: {
        workOrderId: input.woId,
        technician: context.user.id,
        status: 'In Progress',
        workType: input.woType === 'battery_swap' ? 'Battery Swap' : 
                  input.woType === 'wind_audit' ? 'Wind Audit' : 'Work Order',
        initialIssue: input.issue,
        pointCode: input.pointCode,
        lockerVersion: input.lockerVersion,
        client: input.client,
        createdAt: new Date().toISOString(),
      }
    });

    return {
      workOrder: {
        id: workOrder.id,
        workOrderId: workOrder.workOrderId || input.woId,
        status: workOrder.status || 'In Progress',
        workType: workOrder.workType || input.woType,
        initialIssue: workOrder.initialIssue,
        pointCode: workOrder.pointCode,
        lockerVersion: workOrder.lockerVersion,
        client: workOrder.client,
      },
      initialFormType: input.woType,
    };
  },
});

src/api/submitPartsUsed.ts.content:
import { z } from 'zod';
import { createEndpoint, PartsUsed, SyncQueue, WorkOrders } from 'zite-integrations-backend-sdk';

export default createEndpoint({
  authenticated: true,
  description: 'Store parts used and queue for sync to both Airtable bases',
  inputSchema: z.object({
    workOrderId: z.string(),
    parts: z.array(z.object({
      partName: z.string(),
      quantity: z.number(),
    })),
  }),
  outputSchema: z.object({
    success: z.boolean(),
    partIds: z.array(z.string()),
  }),
  execute: async ({ input }) => {
    // Get work order
    const workOrder = await WorkOrders.findOne({
      filters: { workOrderId: input.workOrderId }
    });

    if (!workOrder) {
      throw new Error('Work order not found');
    }

    const partIds: string[] = [];
    const now = new Date().toISOString();

    // Create parts used records
    for (const part of input.parts) {
      const partsUsed = await PartsUsed.create({
        record: {
          workOrder: workOrder.id,
          partName: part.partName,
          quantity: part.quantity,
          recordedAt: now,
        }
      });

      partIds.push(partsUsed.id);

      // Queue for sync to Main Airtable
      await SyncQueue.create({
        record: {
          recordType: 'Parts Used',
          localRecordId: partsUsed.id,
          targetBase: 'Main Airtable',
          payload: JSON.stringify({
            workOrderId: input.workOrderId,
            partName: part.partName,
            quantity: part.quantity,
            recordedAt: now,
          }),
          status: 'Pending',
          attempts: 0,
          createdAt: now,
        }
      });

      // Queue for sync to Asset Management Airtable
      await SyncQueue.create({
        record: {
          recordType: 'Parts Used',
          localRecordId: partsUsed.id,
          targetBase: 'Asset Management',
          payload: JSON.stringify({
            workOrderId: input.workOrderId,
            partName: part.partName,
            quantity: part.quantity,
            recordedAt: now,
          }),
          status: 'Pending',
          attempts: 0,
          createdAt: now,
        }
      });
    }

    return {
      success: true,
      partIds,
    };
  },
});

src/api/completeWorkOrder.ts.content:
import { z } from 'zod';
import { createEndpoint, WorkOrders, SyncQueue } from 'zite-integrations-backend-sdk';

export default createEndpoint({
  authenticated: true,
  description: 'Mark work order as complete and trigger final sync',
  inputSchema: z.object({
    workOrderId: z.string(),
  }),
  outputSchema: z.object({
    success: z.boolean(),
  }),
  execute: async ({ input }) => {
    // Get work order
    const workOrder = await WorkOrders.findOne({
      filters: { workOrderId: input.workOrderId }
    });

    if (!workOrder) {
      throw new Error('Work order not found');
    }

    const now = new Date().toISOString();

    // Mark work order as completed
    await WorkOrders.update({
      id: workOrder.id,
      record: {
        status: 'Completed',
        completedAt: now,
      }
    });

    // Queue work order update for sync
    await SyncQueue.create({
      record: {
        recordType: 'Work Order',
        localRecordId: workOrder.id,
        targetBase: 'Main Airtable',
        payload: JSON.stringify({
          workOrderId: input.workOrderId,
          status: 'Completed',
          completedAt: now,
        }),
        status: 'Pending',
        attempts: 0,
        createdAt: now,
      }
    });

    return {
      success: true,
    };
  },
});

