// Base SDK exports - always available

/** Error class for endpoint errors with typed error codes */
type ZiteErrorCode = 'BAD_REQUEST' | 'UNAUTHORIZED' | 'FORBIDDEN' | 'NOT_FOUND' | 'CONFLICT' | 'RATE_LIMITED' | 'INTERNAL_ERROR';

declare class ZiteError extends Error {
  code: ZiteErrorCode;
  /** Create a ZiteError with a code and message */
  constructor(options: { code: ZiteErrorCode; message: string });
}

/** Context type for endpoint execute functions */
type ZiteRequestContext = {
  user: {
    id: string; // The user's unique record ID. IMPORTANT: Use this value when you need to link records to the current user (e.g., for 'createdBy' or 'owner' linked record fields).
    email: string;
    firstName?: string;
    lastName?: string;
    workOrders?: string[] | string;
    formSubmissions?: string[] | string;
  };
};

/** Stream interface for streaming endpoints */
type ZiteStreamInterface = {
  /** Write a chunk to the stream (sent to frontend in real-time) */
  write: (data: any) => Promise<void>;
  /** Forward an AsyncIterable to the stream and collect the result */
  forward: (asyncIterable: AsyncIterable<string>) => Promise<string>;
};

/** Helper type to infer schema output type */
type InferSchemaType<T> = T extends { _output: infer U } ? U : T;

/** Create a typed backend endpoint */
declare function createEndpoint<TInput, TOutput>(config: {
  /** Optional unique identifier for the endpoint */
  id?: string;
  /** Description of what this endpoint does */
  description?: string;
  /** Whether this endpoint requires authentication (only applies when auth is enabled) */
  authenticated?: boolean;
  /** Enable streaming for this endpoint */
  stream?: boolean;
  /** Zod schema for input validation */
  inputSchema: TInput;
  /** Zod schema for output validation */
  outputSchema: TOutput;
  /** The endpoint implementation */
  execute: (params: {
    input: InferSchemaType<TInput>;
    context: ZiteRequestContext;
    stream?: ZiteStreamInterface;
  }) => Promise<InferSchemaType<TOutput>>;
}): {
  inputSchema: TInput;
  outputSchema: TOutput;
};

export { ZiteError, createEndpoint };
