import { useEffect, useState } from 'react';
import { useAuth } from 'zite-auth-sdk';
import { initWorkOrder, InitWorkOrderOutputType } from 'zite-endpoints-sdk';
import { Loader2 } from 'lucide-react';
import BatterySwapForm from './components/BatterySwapForm';
import WorkOrderFlow from './components/WorkOrderFlow';
import { Toaster } from '@/components/ui/sonner';

export default function App() {
  const { user, isLoading: authLoading } = useAuth();
  const [workOrder, setWorkOrder] = useState<InitWorkOrderOutputType | null>(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);

  useEffect(() => {
    if (authLoading || !user) return;

    const initializeWorkOrder = async () => {
      try {
        // Parse URL parameters
        const params = new URLSearchParams(window.location.search);
        const woId = params.get('WO_ID');
        const woType = params.get('WO_TYPE') as 'battery_swap' | 'wind_audit' | 'work_order' | null;
        const issue = params.get('ISSUE');
        const pointCode = params.get('POINT_CODE');
        const lockerVersion = params.get('LOCKER_VERSION');
        const client = params.get('CLIENT');

        if (!woId) {
          setError('Missing WO_ID parameter');
          setLoading(false);
          return;
        }

        const result = await initWorkOrder({
          woId,
          woType: woType || 'work_order',
          issue: issue || undefined,
          pointCode: pointCode || undefined,
          lockerVersion: lockerVersion || undefined,
          client: client || undefined,
        });

        setWorkOrder(result);
      } catch (err) {
        setError(err instanceof Error ? err.message : 'Failed to initialize work order');
      } finally {
        setLoading(false);
      }
    };

    initializeWorkOrder();
  }, [user, authLoading]);

  if (authLoading || !user) {
    return (
      <div className="min-h-screen flex items-center justify-center bg-background">
        <div className="text-center">
          <Loader2 className="h-8 w-8 animate-spin mx-auto mb-4 text-primary" />
          <p className="text-muted-foreground">Loading...</p>
        </div>
      </div>
    );
  }

  if (loading) {
    return (
      <div className="min-h-screen flex items-center justify-center bg-background">
        <div className="text-center">
          <Loader2 className="h-8 w-8 animate-spin mx-auto mb-4 text-primary" />
          <p className="text-muted-foreground">Initializing work order...</p>
        </div>
      </div>
    );
  }

  if (error || !workOrder) {
    return (
      <div className="min-h-screen flex items-center justify-center bg-background">
        <div className="text-center max-w-md">
          <h1 className="text-2xl font-bold text-destructive mb-2">Error</h1>
          <p className="text-muted-foreground">{error || 'Failed to load work order'}</p>
        </div>
      </div>
    );
  }

  return (
    <>
      <div className="min-h-screen bg-background">
        {workOrder.initialFormType === 'battery_swap' ? (
          <BatterySwapForm workOrder={workOrder.workOrder} />
        ) : (
          <WorkOrderFlow workOrder={workOrder.workOrder} initialFormType={workOrder.initialFormType} />
        )}
      </div>
      <Toaster />
    </>
  );
}

import { useState } from 'react';
import { Button } from '@/components/ui/button';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { submitBatterySwaps } from 'zite-endpoints-sdk';
import { toast } from 'sonner';
import { Plus, Trash2, CheckCircle, Loader2, QrCode } from 'lucide-react';

type BatterySwap = {
  oldBatterySn: string;
  newBatterySn: string;
};

type WorkOrderType = {
  workOrderId: string;
  client?: string;
  pointCode?: string;
};

export default function BatterySwapForm({ workOrder }: { workOrder: WorkOrderType }) {
  const [swaps, setSwaps] = useState<BatterySwap[]>([{ oldBatterySn: '', newBatterySn: '' }]);
  const [submitting, setSubmitting] = useState(false);
  const [completed, setCompleted] = useState(false);

  const addSwap = () => {
    setSwaps([...swaps, { oldBatterySn: '', newBatterySn: '' }]);
  };

  const removeSwap = (index: number) => {
    if (swaps.length > 1) {
      setSwaps(swaps.filter((_, i) => i !== index));
    }
  };

  const updateSwap = (index: number, field: 'oldBatterySn' | 'newBatterySn', value: string) => {
    const updated = [...swaps];
    updated[index][field] = value;
    setSwaps(updated);
  };

  const handleScanQR = async (index: number, field: 'oldBatterySn' | 'newBatterySn') => {
    try {
      // Check if browser supports barcode detection
      if (!('BarcodeDetector' in window)) {
        toast.error('QR scanning not supported in this browser. Please enter manually.');
        return;
      }

      // Request camera access
      const stream = await navigator.mediaDevices.getUserMedia({ video: { facingMode: 'environment' } });

      // Create video element
      const video = document.createElement('video');
      video.srcObject = stream;
      video.play();

      // Wait for video to be ready
      await new Promise(resolve => video.onloadedmetadata = resolve);

      // Create barcode detector
      const barcodeDetector = new (window as any).BarcodeDetector({ formats: ['qr_code'] });

      // Capture frame and detect
      const canvas = document.createElement('canvas');
      canvas.width = video.videoWidth;
      canvas.height = video.videoHeight;
      const ctx = canvas.getContext('2d');
      ctx?.drawImage(video, 0, 0);

      const barcodes = await barcodeDetector.detect(canvas);

      // Stop camera
      stream.getTracks().forEach(track => track.stop());

      if (barcodes.length > 0) {
        updateSwap(index, field, barcodes[0].rawValue);
        toast.success('QR code scanned successfully');
      } else {
        toast.error('No QR code detected. Please try again.');
      }
    } catch (err) {
      toast.error('Failed to access camera. Please enter manually.');
    }
  };

  const handleSubmit = async () => {
    // Validate
    const hasEmpty = swaps.some(s => !s.oldBatterySn.trim() || !s.newBatterySn.trim());
    if (hasEmpty) {
      toast.error('Please fill in all battery serial numbers');
      return;
    }

    setSubmitting(true);
    try {
      await submitBatterySwaps({
        workOrderId: workOrder.workOrderId,
        swaps: swaps.map(s => ({
          oldBatterySn: s.oldBatterySn.trim(),
          newBatterySn: s.newBatterySn.trim(),
        })),
      });

      setCompleted(true);
      toast.success('Battery swaps recorded successfully!');
    } catch (err) {
      toast.error('Failed to submit battery swaps');
    } finally {
      setSubmitting(false);
    }
  };

  if (completed) {
    return (
      <div className="min-h-screen flex items-center justify-center p-4">
        <Card className="max-w-md w-full">
          <CardHeader className="text-center">
            <CheckCircle className="h-16 w-16 text-green-500 mx-auto mb-4" />
            <CardTitle>Work Order Complete</CardTitle>
            <CardDescription>
              Battery swaps have been recorded for work order {workOrder.workOrderId}
            </CardDescription>
          </CardHeader>
        </Card>
      </div>
    );
  }

  return (
    <div className="min-h-screen p-4 md:p-8">
      <div className="max-w-3xl mx-auto">
        <Card>
          <CardHeader>
            <CardTitle>Battery Swap</CardTitle>
            <CardDescription>
              Work Order: {workOrder.workOrderId}
              {workOrder.client && ` â€¢ ${workOrder.client}`}
              {workOrder.pointCode && ` â€¢ ${workOrder.pointCode}`}
            </CardDescription>
          </CardHeader>
          <CardContent className="space-y-6">
            {swaps.map((swap, index) => (
              <div key={index} className="border rounded-lg p-4 space-y-4">
                <div className="flex justify-between items-center">
                  <h3 className="font-medium">Battery #{index + 1}</h3>
                  {swaps.length > 1 && (
                    <Button
                      variant="ghost"
                      size="sm"
                      onClick={() => removeSwap(index)}
                    >
                      <Trash2 className="h-4 w-4" />
                    </Button>
                  )}
                </div>

                <div className="space-y-2">
                  <Label>Old Battery SN</Label>
                  <div className="flex gap-2">
                    <Input
                      value={swap.oldBatterySn}
                      onChange={(e) => updateSwap(index, 'oldBatterySn', e.target.value)}
                      placeholder="Enter or scan serial number"
                    />
                    <Button
                      variant="outline"
                      size="icon"
                      onClick={() => handleScanQR(index, 'oldBatterySn')}
                    >
                      <QrCode className="h-4 w-4" />
                    </Button>
                  </div>
                </div>

                <div className="space-y-2">
                  <Label>New Battery SN</Label>
                  <div className="flex gap-2">
                    <Input
                      value={swap.newBatterySn}
                      onChange={(e) => updateSwap(index, 'newBatterySn', e.target.value)}
                      placeholder="Enter or scan serial number"
                    />
                    <Button
                      variant="outline"
                      size="icon"
                      onClick={() => handleScanQR(index, 'newBatterySn')}
                    >
                      <QrCode className="h-4 w-4" />
                    </Button>
                  </div>
                </div>
              </div>
            ))}

            <Button variant="outline" onClick={addSwap} className="w-full">
              <Plus className="h-4 w-4 mr-2" />
              Add Another Battery
            </Button>

            <Button onClick={handleSubmit} disabled={submitting} className="w-full">
              {submitting ? (
                <>
                  <Loader2 className="h-4 w-4 mr-2 animate-spin" />
                  Submitting...
                </>
              ) : (
                'Complete Work Order'
              )}
            </Button>
          </CardContent>
        </Card>
      </div>
    </div>
  );
}

import { useState } from 'react';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { CheckCircle } from 'lucide-react';
import FilloutFormStep from './FilloutFormStep';
import PartsUsedForm from './PartsUsedForm';

type WorkOrderType = {
  id: string;
  workOrderId: string;
  client?: string;
  pointCode?: string;
  lockerVersion?: string;
  initialIssue?: string;
};

type FormStep = {
  type: 'repair' | 'wind_audit';
  formType: string;
};

export default function WorkOrderFlow({
  workOrder,
  initialFormType,
}: {
  workOrder: WorkOrderType;
  initialFormType: string;
}) {
  const [currentStep, setCurrentStep] = useState(0);
  const [forms, setForms] = useState<FormStep[]>([
    {
      type: initialFormType === 'wind_audit' ? 'wind_audit' : 'repair',
      formType: initialFormType === 'wind_audit' ? 'Wind Audit' : workOrder.initialIssue || 'Repair',
    },
  ]);
  const [showPartsForm, setShowPartsForm] = useState(false);
  const [completed, setCompleted] = useState(false);

  const handleFormComplete = () => {
    if (currentStep < forms.length - 1) {
      setCurrentStep(currentStep + 1);
    } else {
      setShowPartsForm(true);
    }
  };

  const handleAddForm = (formType: 'repair' | 'wind_audit') => {
    setForms([...forms, { type: formType, formType: formType === 'wind_audit' ? 'Wind Audit' : 'Additional Repair' }]);
    setCurrentStep(forms.length);
  };

  const handlePartsComplete = () => {
    setCompleted(true);
  };

  if (completed) {
    return (
      <div className="min-h-screen flex items-center justify-center p-4">
        <Card className="max-w-md w-full">
          <CardHeader className="text-center">
            <CheckCircle className="h-16 w-16 text-green-500 mx-auto mb-4" />
            <CardTitle>Work Order Complete</CardTitle>
            <CardDescription>
              All forms and parts tracking have been submitted for work order {workOrder.workOrderId}
            </CardDescription>
          </CardHeader>
        </Card>
      </div>
    );
  }

  if (showPartsForm) {
    return <PartsUsedForm workOrder={workOrder} onComplete={handlePartsComplete} />;
  }

  const currentForm = forms[currentStep];

  return (
    <div className="min-h-screen p-4 md:p-8">
      <div className="max-w-4xl mx-auto">
        <Card className="mb-6">
          <CardHeader>
            <CardTitle>Work Order: {workOrder.workOrderId}</CardTitle>
            <CardDescription>
              {workOrder.client && `${workOrder.client} â€¢ `}
              {workOrder.pointCode && `${workOrder.pointCode} â€¢ `}
              Form {currentStep + 1} of {forms.length}
            </CardDescription>
          </CardHeader>
        </Card>

        <FilloutFormStep
          workOrder={workOrder}
          formType={currentForm.type}
          formTypeName={currentForm.formType}
          onComplete={handleFormComplete}
          onAddForm={handleAddForm}
        />
      </div>
    </div>
  );
}

import { useState } from 'react';
import { FilloutStandardEmbed } from '@fillout/react';
import { Button } from '@/components/ui/button';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { submitFormResponse } from 'zite-endpoints-sdk';
import { toast } from 'sonner';
import { Plus } from 'lucide-react';

type WorkOrderType = {
  workOrderId: string;
  client?: string;
  pointCode?: string;
  lockerVersion?: string;
  initialIssue?: string;
};

const FORM_IDS = {
  repair: 'tkZbaVzB78us',
  wind_audit: 'tkZbaVzB78us', // TODO: Replace with actual wind audit form ID when available
};

export default function FilloutFormStep({
  workOrder,
  formType,
  formTypeName,
  onComplete,
  onAddForm,
}: {
  workOrder: WorkOrderType;
  formType: 'repair' | 'wind_audit';
  formTypeName: string;
  onComplete: () => void;
  onAddForm: (formType: 'repair' | 'wind_audit') => void;
}) {
  const [showAddOptions, setShowAddOptions] = useState(false);
  const [submitting, setSubmitting] = useState(false);

  const handleSubmit = async (submissionUuid: string) => {
    setSubmitting(true);
    try {
      await submitFormResponse({
        workOrderId: workOrder.workOrderId,
        formType: formTypeName,
        filloutResponseId: submissionUuid,
        formData: {
          submissionUuid,
          formType: formTypeName,
        },
      });

      toast.success('Form submitted successfully!');
      setShowAddOptions(true);
    } catch (err) {
      toast.error('Failed to submit form');
      setSubmitting(false);
    }
  };

  const handleContinue = () => {
    onComplete();
  };

  const handleAddRepair = () => {
    onAddForm('repair');
    setShowAddOptions(false);
  };

  const handleAddWindAudit = () => {
    onAddForm('wind_audit');
    setShowAddOptions(false);
  };

  if (showAddOptions) {
    return (
      <Card>
        <CardHeader>
          <CardTitle>Add More Forms?</CardTitle>
        </CardHeader>
        <CardContent className="space-y-3">
          <Button onClick={handleAddRepair} variant="outline" className="w-full">
            <Plus className="h-4 w-4 mr-2" />
            Add Repair Form
          </Button>
          <Button onClick={handleAddWindAudit} variant="outline" className="w-full">
            <Plus className="h-4 w-4 mr-2" />
            Add Wind Audit Form
          </Button>
          <Button onClick={handleContinue} className="w-full">
            Continue to Parts Tracking
          </Button>
        </CardContent>
      </Card>
    );
  }

  const filloutId = FORM_IDS[formType];
  const parameters = {
    WO_ID: workOrder.workOrderId,
    CLIENT: workOrder.client || '',
    POINT_CODE: workOrder.pointCode || '',
    LOCKER_VERSION: workOrder.lockerVersion || '',
    ISSUE: workOrder.initialIssue || '',
  };

  return (
    <Card>
      <CardHeader>
        <CardTitle>{formTypeName}</CardTitle>
      </CardHeader>
      <CardContent>
        <div className="w-full" style={{ minHeight: '600px' }}>
          <FilloutStandardEmbed
            filloutId={filloutId}
            parameters={parameters}
            dynamicResize
            onSubmit={handleSubmit}
          />
        </div>
      </CardContent>
    </Card>
  );
}

import { useState } from 'react';
import { Button } from '@/components/ui/button';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { submitPartsUsed, completeWorkOrder } from 'zite-endpoints-sdk';
import { toast } from 'sonner';
import { Plus, Trash2, Loader2 } from 'lucide-react';

type Part = {
  partName: string;
  quantity: number;
};

type WorkOrderType = {
  workOrderId: string;
};

export default function PartsUsedForm({
  workOrder,
  onComplete,
}: {
  workOrder: WorkOrderType;
  onComplete: () => void;
}) {
  const [parts, setParts] = useState<Part[]>([{ partName: '', quantity: 1 }]);
  const [submitting, setSubmitting] = useState(false);

  const addPart = () => {
    setParts([...parts, { partName: '', quantity: 1 }]);
  };

  const removePart = (index: number) => {
    if (parts.length > 1) {
      setParts(parts.filter((_, i) => i !== index));
    }
  };

  const updatePart = (index: number, field: 'partName' | 'quantity', value: string | number) => {
    const updated = [...parts];
    if (field === 'partName') {
      updated[index].partName = value as string;
    } else {
      updated[index].quantity = value as number;
    }
    setParts(updated);
  };

  const handleSubmit = async () => {
    // Filter out empty parts
    const validParts = parts.filter(p => p.partName.trim());

    if (validParts.length === 0) {
      toast.error('Please add at least one part');
      return;
    }

    setSubmitting(true);
    try {
      // Submit parts
      await submitPartsUsed({
        workOrderId: workOrder.workOrderId,
        parts: validParts.map(p => ({
          partName: p.partName.trim(),
          quantity: p.quantity,
        })),
      });

      // Complete work order
      await completeWorkOrder({
        workOrderId: workOrder.workOrderId,
      });

      toast.success('Parts tracked and work order completed!');
      onComplete();
    } catch (err) {
      toast.error('Failed to submit parts');
      setSubmitting(false);
    }
  };

  const handleSkip = async () => {
    setSubmitting(true);
    try {
      // Complete work order without parts
      await completeWorkOrder({
        workOrderId: workOrder.workOrderId,
      });

      toast.success('Work order completed!');
      onComplete();
    } catch (err) {
      toast.error('Failed to complete work order');
      setSubmitting(false);
    }
  };

  return (
    <div className="min-h-screen p-4 md:p-8">
      <div className="max-w-3xl mx-auto">
        <Card>
          <CardHeader>
            <CardTitle>Parts Used</CardTitle>
            <CardDescription>
              Track all parts used for work order {workOrder.workOrderId}
            </CardDescription>
          </CardHeader>
          <CardContent className="space-y-6">
            {parts.map((part, index) => (
              <div key={index} className="border rounded-lg p-4 space-y-4">
                <div className="flex justify-between items-center">
                  <h3 className="font-medium">Part #{index + 1}</h3>
                  {parts.length > 1 && (
                    <Button
                      variant="ghost"
                      size="sm"
                      onClick={() => removePart(index)}
                    >
                      <Trash2 className="h-4 w-4" />
                    </Button>
                  )}
                </div>

                <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                  <div className="space-y-2">
                    <Label>Part Name</Label>
                    <Input
                      value={part.partName}
                      onChange={(e) => updatePart(index, 'partName', e.target.value)}
                      placeholder="Enter part name"
                    />
                  </div>

                  <div className="space-y-2">
                    <Label>Quantity</Label>
                    <Input
                      type="number"
                      min="1"
                      value={part.quantity}
                      onChange={(e) => updatePart(index, 'quantity', parseInt(e.target.value) || 1)}
                    />
                  </div>
                </div>
              </div>
            ))}

            <Button variant="outline" onClick={addPart} className="w-full">
              <Plus className="h-4 w-4 mr-2" />
              Add Another Part
            </Button>

            <div className="flex gap-3">
              <Button
                variant="outline"
                onClick={handleSkip}
                disabled={submitting}
                className="flex-1"
              >
                Skip (No Parts Used)
              </Button>
              <Button onClick={handleSubmit} disabled={submitting} className="flex-1">
                {submitting ? (
                  <>
                    <Loader2 className="h-4 w-4 mr-2 animate-spin" />
                    Submitting...
                  </>
                ) : (
                  'Complete Work Order'
                )}
              </Button>
            </div>
          </CardContent>
        </Card>
      </div>
    </div>
  );
}

